DROP TABLE IF EXISTS `films_local`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `films_local` (
  `loc_id` int(11) NOT NULL,
  `loc_lang` varchar(2) NOT NULL,
  `loc_name` varchar(30) NOT NULL,
  `loc_direct` varchar(40) NOT NULL,
  `loc_country` set('США','Великобритания','Аргентина','Австралия','Австрия','Беларусь','Бельгия','Бразилия','Болгария','Канада','Чили','Китай','Колумбия','Хорватия','Куба','Чехия','Египт','Финляндия','Франция','Грузия','Германия','Греция','Венгрия','Исландия','Индия','Индонезия','Ирак','Иран','Ирландия','Израиль','Италия','Япония','Южная Корея','Латвия','Ливан','Литва','Мадагаскар','Мексика','Молдова','Монако','Нидерланды','Новая Зеландия','Норвегия','Перу','Филиппины','Польша','Португалия','Румыния','Рим','Россия','Сербия','Сингапур','Словакия','Словения','ЮАР','Испания','Швеция','Швейцария','Сирия','Турция','Украина','Вьетнам') DEFAULT NULL,
  `loc_genre` set('Биография','Боевик','Вестерн','Военный','Детектив','Документальный','Драма','История','Комедия','Криминал','Мелодрама','Мистика','Музыка','Мультфильм','Мюзикл','Нуар','Приключения','Семейный','Спорт','Триллер','Фэнтези','Ужасы','Фантастика') DEFAULT NULL,
  `loc_actors` varchar(120) DEFAULT NULL,
  `loc_composer` varchar(75) DEFAULT NULL,
  `loc_description` varchar(2000) DEFAULT NULL,
  PRIMARY KEY (`loc_id`,`loc_lang`),
  KEY `f_lang_idx` (`loc_lang`),
  CONSTRAINT `loc_id` FOREIGN KEY (`loc_id`) REFERENCES `films` (`f_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `loc_lang` FOREIGN KEY (`loc_lang`) REFERENCES `langs` (`l_code`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

